    Module to make requests

   ``` // make_request.js var http = require('http');

   var makeRequest = function(message) { var options = { host:
   'localhost', port: 8080, path:'/', method: 'POST' } var request =
   http.request(options, function(response) {
   response.on('data',function(data){ console.log(data); }); });
   request.write(message); request.end(); }); } exports = makeRequest; How
